    b1 = [1.8680896 -4.1543913 -3.1527512 1.4673438 1.0731114 3.1156878 -1.1042624 -0.53752357 1.9296689 -6.523799 0.74826276 -0.8035014 -2.5839558 -7.187756 7.211089 -3.2320046 -2.4105961 -5.944899 -3.9314053 -3.5495036 0.20619696 -3.9368286 -2.163769 -4.760829];
    w2 = [1.2917049;-0.37564796;-0.6884577;0.2766755;-0.42947832;0.11994787;1.015592;1.6894923;-0.50503504;-2.0209548;-0.2658867;-1.1003923;-7.591591;-9.337168;-0.08774139;-1.2791088;-0.19651034;-0.44430938;3.8216915;1.5113724;-1.1928473;7.1026397;0.042860758;0.03166003];
    w3 = [0.036515303 -3.6093485;-1.631062 -3.7364223;-5.2002616 2.0071523;-2.2761667 -0.99380445;-1.5476027 -2.2162468;1.1789608 -4.9868016;-0.5381897 -1.40446;-1.7835476 2.9217372;1.0723449 -5.0468225;-5.8968763 -7.675129;-3.4932432 -2.0608225;-0.10844406 4.2605486;-2.210804 -2.0054393;-11.8500395 8.553857;4.402251 -4.007865;-4.103229 1.2034284;-5.07486 4.35026;-0.38162172 4.5918193;7.0316305 3.439235;-5.6793547 -0.41916996;-0.74578756 2.466518;-2.2151375 8.076859;2.8038902 -5.0026665;-1.3425066 3.5862548];
