    input = [VELX - means(1),VELY,VELROTZ,BETA,AB - means(5),TV];

    normed_input = input ./ stds;
    normed_input_neg = normed_input .* [1,-1,-1,-1,1,-1];

    h1 = log(exp(normed_input * w1 + b1) + 1);
    h1_neg = log(exp(normed_input_neg * w1 + b1) + 1);
    
    h1_even = (h1 + h1_neg) / 2;
