    ACC_odd = h1_odd * w3;
    
    ACCX = ACCX_NOM + h1_even * w2 + b2;
    ACCY = ACCY_NOM + ACC_odd(1);
    ACCROTZ = ACCROTZ_NOM + ACC_odd(2);
end

